mapper.select:
select
from cmdb_resource_cabinet a, cmdb_resource_floor b where a.floor_id = b.floor_id
and b.floor_id =#{floorId}
and a.cabinet_scope = #{cabinetScope}
and a.cabinet_name=#{cabinetName}
and cabinet_id in (select distinct cabinet_id from cmdb_resource_server where cabinet_id>1 )
and cabinet_id not in (select distinct cabinet_id from cmdb_resource_server where cabinet_id>1 )
and cabinet_id in (select cabinet_id from cmdb_resource_server where ip_address like concat('%',#{search},'%'))
and cabinet_id in (select distinct cabinet_id from cmdb_resource_server where cabinet_id>1 and status=0 )
order by cabinet_id desc

select distinct cabinet_scope as cabinetScope, cabinet_id as cabinetId, b.floor_name as floorName from cmdb_resource_cabinet a, cmdb_resource_floor b where cabinet_id!=1 and a.floor_id = b.floor_id
and cabinet_scope=#{cabinetScope}
and cabinet_name=#{cabinetName}
and cabinet_id in (select distinct cabinet_id from cmdb_resource_server where cabinet_id>1 )
